import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Make sure to import axios
import './Home.css';

function Home({ id }) { // Assume `id` is passed as a prop to the Home component
  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    profilePicture: null,
  });

  // Fetch user data from the server
  const fetchUserData = async () => {
    try {
      const response = await axios.get(`http://localhost:8087/api/get/${id}`);
      setFormData({
        fullName: response.data[0].name, // Adjust based on your database schema
        username: response.data[0].email, // Assuming username is stored as email
        profilePicture: null, // Set this based on your application logic
      });
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  useEffect(() => {
    fetchUserData();
  }, [id]); // Fetch user data whenever `id` changes

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
  };

  return (
    <div className='home'>
      <div className="app-container">
        <div className="profile-card">
          <div className="profile-image">
            <img
              src={formData.profilePicture ? URL.createObjectURL(formData.profilePicture) : "https://via.placeholder.com/150"}
              alt="Profile"
            />
          </div>
          <h2>{formData.fullName}</h2>
          <div className="buttons">
            <button className="logout-btn">Logout</button>
          </div>
        </div>

        <div className="edit-profile-form">
          <h2 className='home_h1'>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            <label>Full Name</label>
            <input 
              type="text" 
              name="fullName"
              className='home_input' 
              value={formData.fullName} 
              onChange={handleChange} 
            />
            
            <label>Username</label>
            <input 
              type="text" 
              name="username" 
              className='home_input' 
              value={formData.username} 
              onChange={handleChange} 
            />
            
            <label>Profile Picture</label>
            <input 
              type="file" 
              name="profilePicture" 
              className='home_input' 
              onChange={handleFileChange} 
            />
            
            <button type="submit" className="update-btn">Update</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Home;
