// src/App.js
import React, { useState } from 'react';
import './Home.css';

function Home() {
  // State to manage form data
  const [formData, setFormData] = useState({
    fullName: 'Elias',
    username: 'elias',
    profilePicture: null,
  });

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle profile picture file change
  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic (e.g., saving to backend)
    console.log('Form Data:', formData);
  };

  return (
    <div className="app-container">
      {/* Left Section: Profile Card */}
      <div className="profile-card">
        <div className="profile-image">
          <img
            src={formData.profilePicture ? URL.createObjectURL(formData.profilePicture) : "https://via.placeholder.com/150"}
            alt="Profile"
          />
        </div>
        <h2>{formData.fullName}</h2>
        <div className="buttons">
          <button className="edit-btn">Edit Profile</button>
          <button className="logout-btn">Logout</button>
        </div>
      </div>

      {/* Right Section: Edit Profile Form */}
      <div className="edit-profile-form">
        <h2 className='home_h1'>Edit Profile</h2>
        <form onSubmit={handleSubmit}>
          <label>Full Name</label>
          <input 
            type="text" 
            name="fullName"
            className='home_input' 
            value={formData.fullName} 
            onChange={handleChange} 
          />
          
          <label>Username</label>
          <input 
            type="text" 
            name="username" 
            className='home_input' 
            value={formData.username} 
            onChange={handleChange} 
          />
          
          <label>Profile Picture</label>
          <input 
            type="file" 
            name="profilePicture" 
            className='home_input' 
            onChange={handleFileChange} 
          />
          
          <button type="submit" className="update-btn">Update</button>
          <button className="home-btn">Home</button>
        </form>
      </div>
    </div>
  );
}

export default Home;
