import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';  // Import useParams
import axios from 'axios'; // Import Axios for API calls
import './Home.css';

function Home() {
  const [user, setUser] = useState({});
  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    profilePicture: null
  });

  const { id } = useParams();  // Destructure id from useParams

  useEffect(() => { 
    axios.get(`http://localhost:8087/api/get/${id}`)
      .then((resp) => {
        setUser(resp.data[0]);
        setFormData({
          fullName: resp.data[0].name,
          username: resp.data[0].username,
          profilePicture: null
        });
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Add the logic for updating the user profile, such as making an API call.
  };

  return (
    <div className='home'>
      <div className="app-container">
        <div className="profile-card">
          <div className="profile-image">
            <img
              src={formData.profilePicture ? URL.createObjectURL(formData.profilePicture) : "https://via.placeholder.com/150"}
              alt="Profile"
            />
          </div>
          <h2>{formData.fullName}</h2>
          <div className="buttons">
            <button className="logout-btn">Logout</button>
          </div>
          
          <div className="user-info">
            <p className='viewp'><strong>ID:</strong> {id}</p>
            <p className='viewp'><strong>Name:</strong> {user.name}</p>
            <p className='viewp'><strong>Email:</strong> {user.email}</p>
          </div>
        </div>

        <div className="edit-profile-form">
          <h2 className='home_h1'>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            <label>Full Name</label>
            <input 
              type="text" 
              name="fullName"
              className='home_input' 
              value={formData.fullName} 
              onChange={handleChange} 
            />
            
            <label>Username</label>
            <input 
              type="text" 
              name="username" 
              className='home_input' 
              value={formData.username} 
              onChange={handleChange} 
            />
            
            <label>Profile Picture</label>
            <input 
              type="file" 
              name="profilePicture" 
              className='home_input' 
              onChange={handleFileChange} 
            />
            
            <button type="submit" className="update-btn">Update</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Home;
