import React, { useState, useEffect } from 'react';
import './Home.css';

function Home() {
  const [formData, setFormData] = useState({
    fullName: 'Elias',
    username: 'elias',
    profilePicture: null,
  });

  const [userInfo, setUserInfo] = useState({
    id: '',
    email: '',
  });

  const userId = '12345'; // Replace with actual user ID (can be stored in state or obtained from context)

  // Fetch user info when the component mounts
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:8087/user/${userId}`); // Adjust the URL to your API endpoint
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setUserInfo(data);
        setFormData((prevData) => ({
          ...prevData,
          fullName: data.fullName || prevData.fullName, // Update fullName if it exists in the data
          username: data.username || prevData.username, // Update username if it exists in the data
        }));
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Logic to update user info on the backend goes here
  };

  return (
    <div className='home'>
      <div className="app-container">
        <div className="profile-card">
          <div className="profile-image">
            <img
              src={formData.profilePicture ? URL.createObjectURL(formData.profilePicture) : "https://via.placeholder.com/150"}
              alt="Profile"
            />
          </div>
          <h2>{formData.fullName}</h2>
          <div className="buttons">
            <button className="logout-btn">Logout</button>
          </div>
          
          {/* Displaying User ID and Email */}
          <div className="user-info">
            <p>User ID: {userInfo.id}</p>
            <p>Email: {userInfo.email}</p>
          </div>
        </div>

        <div className="edit-profile-form">
          <h2 className='home_h1'>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            <label>Full Name</label>
            <input 
              type="text" 
              name="fullName"
              className='home_input' 
              value={formData.fullName} 
              onChange={handleChange} 
            />
            
            <label>Username</label>
            <input 
              type="text" 
              name="username" 
              className='home_input' 
              value={formData.username} 
              onChange={handleChange} 
            />
            
            <label>Profile Picture</label>
            <input 
              type="file" 
              name="profilePicture" 
              className='home_input' 
              onChange={handleFileChange} 
            />
            
            <button type="submit" className="update-btn">Update</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Home;
