import React, { useState, useEffect } from 'react';
import './Home.css';

function Home() {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    profilePicture: null,
  });

  const [userInfo, setUserInfo] = useState({
    id: '',
    email: '',
    name: '',
  });

  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(''); // Error state

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token'); // Get the JWT from local storage
        const response = await fetch('http://localhost:8087/users', {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`, // Include the token in the request headers
            'Content-Type': 'application/json', // Set content type to JSON
          },
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();

        // Assuming you want the first user in the returned array
        if (data.length > 0) {
          setUserInfo({
            id: data[0].id, // Adjust based on your DB structure
            email: data[0].email,
            name: data[0].name,
          });

          // Set form data with fetched user data
          setFormData({
            fullName: data[0].name, // Assuming name is in your DB
            email: data[0].email || '', // Adjust based on your DB structure
            profilePicture: null, // Start with no profile picture
          });
        }
      } catch (error) {
        setError('Error fetching user data'); // Set error state
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false); // Set loading to false when done
      }
    };

    fetchUserData();
  }, []); // Empty dependency array means this effect runs once when the component mounts

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);

    // Here, you might want to implement the logic for updating the user data.
    // For example, you can send the updated data to your API:
    const updatedData = new FormData();
    updatedData.append('fullName', formData.fullName);
    updatedData.append('email', formData.email);
    if (formData.profilePicture) {
      updatedData.append('profilePicture', formData.profilePicture);
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8087/users', {
        method: 'PUT', // Use PUT or PATCH based on your API
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: updatedData,
      });

      if (!response.ok) {
        throw new Error('Failed to update user data');
      }

      const result = await response.json();
      console.log('User updated successfully:', result);
      // Optionally, you can update userInfo with the new data if needed
    } catch (error) {
      setError('Error updating user data');
      console.error('Error updating user data:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear token
    // Redirect to login page
    window.location.href = '/login'; // Use history.push('/login') if using react-router
  };

  if (loading) return <div>Loading...</div>; // Show loading state
  if (error) return <div>{error}</div>; // Show error message

  return (
    <div className='home'>
      <div className="app-container">
        <div className="profile-card">
          <div className="profile-image">
            <img
              src={formData.profilePicture ? URL.createObjectURL(formData.profilePicture) : "https://via.placeholder.com/150"}
              alt="Profile"
            />
          </div>
          <h2>{formData.fullName}</h2>
          <div className="user-info">
            <p className='user_data'>User ID: {userInfo.id}</p>
            <p className='user_data'>Name: {userInfo.name}</p>
            <p className='user_data'>Email: {userInfo.email}</p>
          </div>
          <div className="buttons">
            <button className="logout-btn" onClick={handleLogout}>Logout</button>
          </div>
        </div>

        <div className="edit-profile-form">
          <h2 className='home_h1'>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            <label>Full Name</label>
            <input 
              type="text" 
              name="fullName"
              className='home_input' 
              value={formData.fullName} 
              onChange={handleChange} 
            />
            
            <label>Email</label>
            <input 
              type="email" // Changed to email type for better validation
              name="email"
              className='home_input' 
              value={formData.email} 
              onChange={handleChange} 
            />
            
            <label>Profile Picture</label>
            <input 
              type="file" 
              name="profilePicture" 
              className='home_input' 
              onChange={handleFileChange} 
            />
            
            <button type="submit" className="update-btn">Update</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Home;
