import React, { useState } from 'react';
import './SignUp.css';
import background from '../../assets/bg.svg';
import avatar from '../../assets/avatar.svg';
import wave from '../../assets/wave.png';
import { ToastContainer, toast } from 'react-toastify'; 
import 'react-toastify/dist/ReactToastify.css'; 
import axios from 'axios'; // Make sure to import axios

const SignUp = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errors, setErrors] = useState({}); // Object to hold error messages

  const validate = () => {
    const validationErrors = {};
    if (!name) validationErrors.name = 'Name is required.';
    if (!email) validationErrors.email = 'Email is required.';
    if (!password) validationErrors.password = 'Password is required.';
    if (password !== confirmPassword) validationErrors.confirmPassword = 'Passwords do not match.';
    return validationErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission
    const validationErrors = validate(); // Run validation

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors); // Set errors if there are any
      return; // Exit the function if there are validation errors
    }

    // Clear errors if validation passes
    setErrors({});

    // API call to the backend
    try {
      const response = await axios.post('http://localhost:8087/signup', {
        name,
        email,
        password,
      });

      if (response.status === 200) {
        toast.success('Sign-up successful!'); // Success message
        window.location.href = '/signin'; // Redirect to sign-in page
      }
    } catch (error) {
      console.error('Error:', error);
      // Handle error response from server
      setErrors({ general: 'An error occurred. Please try again later.' });
    }
  };

  return (
    <div>
      <img className="wave" src={wave} alt="wave" />
      <div className="container">
        <div className="img">
          <h1 className="title1">SIGN UP</h1>
          <img src={background} alt="background" />
        </div>
        <div className="login-content">
          <form onSubmit={handleSubmit}>
            <img src={avatar} alt="avatar" />
            <h2 className="title">Welcome</h2>

            {errors.general && <p className="error-message">{errors.general}</p>} {/* General error */}

            <div className="input-div one">
              <div className="i">
                <i className="fas fa-user"></i>
              </div>
              <div className="div">
                <input 
                  type="text" 
                  className="input" 
                  placeholder='Name'
                  value={name}
                  onChange={(e) => setName(e.target.value)} 
                />
                {errors.name && <p className="error-message">{errors.name}</p>} {/* Name error */}
              </div>
            </div>

            <div className="input-div one">
              <div className="i">
                <i className="fas fa-user"></i>
              </div>
              <div className="div">
                <input 
                  type="email" 
                  className="input" 
                  placeholder='Email'
                  value={email}
                  onChange={(e) => setEmail(e.target.value)} 
                />
                {errors.email && <p className="error-message">{errors.email}</p>} {/* Email error */}
              </div>
            </div>

            <div className="input-div pass">
              <div className="i">
                <i className="fas fa-lock"></i>
              </div>
              <div className="div">
                <input 
                  type="password" 
                  className="input" 
                  placeholder='Password'
                  value={password}
                  onChange={(e) => setPassword(e.target.value)} 
                />
                {errors.password && <p className="error-message">{errors.password}</p>} {/* Password error */}
              </div>
            </div>

            <div className="input-div pass">
              <div className="i">
                <i className="fas fa-lock"></i>
              </div>
              <div className="div">
                <input 
                  type="password" 
                  className="input" 
                  placeholder='Confirm Password'
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)} 
                />
                {errors.confirmPassword && <p className="error-message">{errors.confirmPassword}</p>} {/* Confirm Password error */}
              </div>
            </div>

            <a href="#">Forgot Password?</a>
            <input type="submit" className="btn" value="Sign Up" /> 
            <a href='/signin' className="abtn">SIGN IN</a>
            <p>Already Have An Account?</p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
